function contactFilter() {
        var input, filter, ul, li, a, i, txtValue;
        input = document.getElementById('contact_list_search');
		h3 = document.getElementsByClassName('md-card-head-text uk-text-center');
        filter = input.value.toUpperCase();
		
        for (i = 0; i < h3.length; i++) {
            txtValue = h3[i].textContent || h3[i].innerText;
            if (txtValue.toUpperCase().indexOf(filter) > -1) {
            h3[i].parentNode.parentNode.parentNode.parentNode.style.display = "";
            } else {
            h3[i].parentNode.parentNode.parentNode.parentNode.style.display = "none";
            }
		}
}